js.utils.registerNamespace("eu.europeana.ess");eu.europeana.ess={init:function(){var a=this;jQuery(".external-services").each(function(b){jQuery(this).bind("click",{self:a},a.handleEssClick);});},handleEssClick:function(a){a.preventDefault();a.data.self.showEssOptions(jQuery(this));},showEssOptions:function(a){var b=this;if(a.siblings(".external-services-container").length>0){b.closeEssOptions(a.siblings(".external-services-container"));return;}jQuery.get(a.attr("href"),function(c){a.parent().append(c);a.siblings(".external-services-container").slideDown();a.addClass("active");jQuery(a.siblings(".external-services-container").children(".header").children(".close-button")).add(a).click(function(d){d.preventDefault();b.closeEssOptions(a);});});},closeEssOptions:function(a){a.removeClass("active");a.siblings(".external-services-container").slideUp(function(){a.siblings(".external-services-container").remove();});}};